mapper.insert:
INSERT INTO acc
	VALUES (
		#{BUSINESS_NO}
		,#{CUST_NAME}
		,#{BOSS_NAME}
		,#{UPTAE}
		,#{JONGMOK}
		,

				''


				CONCAT(#{TEL1},'-',#{TEL2},'-',#{TEL3})


		,


				''


				CONCAT(#{EMAIL1},'@',#{EMAIL2})


		,#{POST_NO}
		,CONCAT(#{ADDR1},',',#{ADDR2})
		,#{G_GUBUN}
		,#{FAX}
		, 

				''


				CONCAT(#{MOBILE_NO1},'-',#{MOBILE_NO2},'-',#{MOBILE_NO3})


		,#{URL_PATH}
		,#{MAN_NAME}
		, 

				''


				CONCAT(#{MAN_TEL1},'-',#{MAN_TEL2},'-',#{MAN_TEL3})


		, 

				''


				CONCAT(#{MAN_EMAIL1},'@',#{MAN_EMAIL2})


		,#{REMARKS}
	)

mapper.update:
UPDATE acc
	SET
		 CUST_NAME = #{CUST_NAME}
		,BOSS_NAME = #{BOSS_NAME}
		,UPTAE = #{UPTAE}
		,JONGMOK = #{JONGMOK}
		,TEL =  


				''


				CONCAT(#{TEL1},'-',#{TEL2},'-',#{TEL3})


		,EMAIL = 


				''


				CONCAT(#{EMAIL1},'@',#{EMAIL2})


		,POST_NO = #{POST_NO}
		,ADDR = 


				''


				CONCAT(#{ADDR1},',',#{ADDR2})


		,G_GUBUN = #{G_GUBUN}
		,FAX = #{FAX}
		,MOBILE_NO = 


				''


				CONCAT(#{MOBILE_NO1},'-',#{MOBILE_NO2},'-',#{MOBILE_NO3})


		,MAN_NAME = #{MAN_NAME}
		,MAN_EMAIL = 


				''


				CONCAT(#{MAN_EMAIL1},'@',#{MAN_EMAIL2})


		,MAN_TEL = #{MAN_TEL}
		,REMARKS = #{REMARKS}
			WHERE BUSINESS_NO = #{BUSINESS_NO}
